package testfactory

import kotlin.test.Test
import kotlin.test.assertEquals
import kotlin.test.assertTrue

class LibraryTest {
    data class Foo(val bar: Int, val baz: String)

    class Factory<T> {
        fun build(): Foo {
            return Foo(1, "")
        }
    }

    @Test fun canBuildDataClass() {
        val foo = Factory<Foo>().build()
        assertEquals(foo, Foo(1, ""))
    }
}
